const items = $input.all();
const now = new Date();
const readyPosts = [];

for (const item of items) {
  const row = item.json;

  // Check if post is scheduled and not yet published
  const status = (row.status || '').toLowerCase();
  if (status !== 'scheduled' && status !== 'ready') continue;

  // Check scheduled time
  const scheduledTime = row.scheduled_time || row.scheduledTime;
  if (scheduledTime) {
    const schedDate = new Date(scheduledTime);
    if (schedDate > now) continue; // Not yet time
  }

  // Validate required fields
  if (!row.content && !row.text && !row.message) continue;

  const platforms = (row.platforms || row.platform || 'twitter,linkedin')
    .split(',')
    .map(p => p.trim().toLowerCase());

  readyPosts.push({
    json: {
      id: row.id || row.row_number || Date.now(),
      content: row.content || row.text || row.message,
      platforms: platforms,
      imageUrl: row.image_url || row.imageUrl || null,
      scheduledTime: scheduledTime,
      category: row.category || 'general',
      campaign: row.campaign || '',
      hashtags: row.hashtags || '',
      linkUrl: row.link_url || row.linkUrl || '',
      tone: row.tone || 'professional'
    }
  });
}

if (readyPosts.length === 0) {
  return [{ json: { noContent: true, message: 'No posts ready to publish' } }];
}

return readyPosts;

