const input = $input.first().json;
const originalData = $('Filter Ready Posts').item.json;

let optimized;
try {
  const responseText = input.output || input.text || '';
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  optimized = jsonMatch ? JSON.parse(jsonMatch[0]) : null;
} catch (e) {
  optimized = null;
}

if (!optimized) {
  // Fallback to original content
  optimized = {
    twitter: {
      text: originalData.content.substring(0, 280),
      hashtags: originalData.hashtags ? originalData.hashtags.split(' ') : []
    },
    linkedin: {
      text: originalData.content,
      hashtags: originalData.hashtags ? originalData.hashtags.split(' ') : []
    },
    engagementTips: [],
    bestPostTime: 'Now',
    contentScore: 70
  };
}

// Build final posts
const twitterText = optimized.twitter.text + 
  (optimized.twitter.hashtags?.length > 0 ? ' ' + optimized.twitter.hashtags.map(h => h.startsWith('#') ? h : '#' + h).join(' ') : '');

const linkedinText = optimized.linkedin.text + 
  (optimized.linkedin.hashtags?.length > 0 ? '\n\n' + optimized.linkedin.hashtags.map(h => h.startsWith('#') ? h : '#' + h).join(' ') : '');

return [{
  json: {
    ...originalData,
    optimized: optimized,
    posts: {
      twitter: {
        text: twitterText.substring(0, 280),
        ready: originalData.platforms.includes('twitter')
      },
      linkedin: {
        text: linkedinText,
        ready: originalData.platforms.includes('linkedin')
      }
    },
    contentScore: optimized.contentScore || 70,
    processedAt: new Date().toISOString()
  }
}];

const results = $input.first().json.data || [];
const originalData = $('Parse AI Content').first().json;

const postResults = {
  twitter: null,
  linkedin: null
};

for (const result of results) {
  if (result.id_str || result.data?.id) {
    postResults.twitter = {
      success: true,
      postId: result.id_str || result.data?.id,
      url: `https://twitter.com/i/status/${result.id_str || result.data?.id}`
    };
  }
  if (result.id && String(result.id).includes('urn:li:share')) {
    postResults.linkedin = {
      success: true,
      postId: result.id,
      url: `https://linkedin.com/feed/update/${result.id}`
    };
  }
}

const successCount = Object.values(postResults).filter(p => p?.success).length;
const totalPlatforms = originalData.platforms.length;

return [{
  json: {
    contentId: originalData.id,
    originalContent: originalData.content.substring(0, 100) + '...',
    platforms: originalData.platforms,
    postResults: postResults,
    summary: {
      successCount: successCount,
      totalPlatforms: totalPlatforms,
      allSuccessful: successCount === totalPlatforms
    },
    contentScore: originalData.contentScore,
    publishedAt: new Date().toISOString()
  }
}];
