// Only hash what defines the schema
const schemaText = $input.first().json['Create Table']

let hash = 5381;
for (let i = 0; i < schemaText.length; i++) {
  hash = ((hash << 5) + hash) + schemaText.charCodeAt(i);
}

return {
  ...$input.first().json,
  vector_id: $('Set Table Schema Context').first().json.vector_id,
  source_type: $('Set Table Schema Context').first().json.source_type,
  schema_hash: hash.toString()
};
