// Prepare audit data for AI agent
const auditData = $input.first().json;

// Extract key metrics
const instanceReport = auditData['Instance Risk Report'];
const credentialsReport = auditData['Credentials Risk Report'];
const databaseReport = auditData['Database Risk Report'];
const nodesReport = auditData['Nodes Risk Report'];
const filesystemReport = auditData['Filesystem Risk Report'];

// Count issues
const unusedCredentials = credentialsReport?.sections?.[0]?.location?.length || 0;
const inactiveCredentials = credentialsReport?.sections?.[1]?.location?.length || 0;
const staleCredentials = credentialsReport?.sections?.[2]?.location?.length || 0;
const sqlIssues = databaseReport?.sections?.[0]?.location?.length || 0;
const riskyNodes = nodesReport?.sections?.[0]?.location?.length || 0;
const communityNodes = nodesReport?.sections?.[1]?.location?.length || 0;
const filesystemNodes = filesystemReport?.sections?.[0]?.location?.length || 0;

// Calculate risk level
const totalIssues = unusedCredentials + sqlIssues + riskyNodes + filesystemNodes;
let riskLevel = 'LOW';
if (totalIssues > 20) riskLevel = 'HIGH';
else if (totalIssues > 10) riskLevel = 'MEDIUM';

// Build structured summary for AI
const summary = {
  metrics: {
    unusedCredentials,
    inactiveCredentials,
    staleCredentials,
    sqlInjectionRisks: sqlIssues,
    riskyOfficialNodes: riskyNodes,
    communityNodes,
    filesystemNodes,
    totalIssues,
    riskLevel
  },
  instanceSettings: instanceReport?.sections?.[0]?.settings || {},

  // Unused credentials list
  unusedCredentialsList: credentialsReport?.sections?.[0]?.location?.map(c => c.name) || [],

  // SQL issues with workflow context
  sqlIssuesList: databaseReport?.sections?.[0]?.location?.map(n => ({
    workflow: n.workflowName,
    node: n.nodeName
  })) || [],

  // Group risky nodes by workflow
  riskyNodesByWorkflow: {},

  // Community packages
  communityPackages: nodesReport?.sections?.[1]?.location?.map(p => {
    const name = p.packageUrl.split('/package/')[1] || p.nodeType;
    return { name, url: p.packageUrl };
  }) || [],

  // Filesystem nodes
  filesystemNodesList: filesystemReport?.sections?.[0]?.location?.map(n => ({
    workflow: n.workflowName,
    node: n.nodeName,
    type: n.nodeType
  })) || []
};

// Group risky nodes by workflow for better AI analysis
if (nodesReport?.sections?.[0]?.location) {
  for (const node of nodesReport.sections[0].location) {
    if (!summary.riskyNodesByWorkflow[node.workflowName]) {
      summary.riskyNodesByWorkflow[node.workflowName] = [];
    }
    summary.riskyNodesByWorkflow[node.workflowName].push({
      name: node.nodeName,
      type: node.nodeType.split('.').pop()
    });
  }
}

// Create the prompt context
const auditContext = JSON.stringify(summary, null, 2);

return {
  json: {
    auditContext,
    summary,
    generatedAt: new Date().toISOString(),
    reportDate: new Date().toLocaleDateString('en-US', { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric'
    })
  }
};

// Format the AI output for Google Doc
const aiOutput = $input.first().json.output;
const prepData = $('Prepare Audit Data').first().json;

// Build the final report document
let reportContent = aiOutput.fullReport;

// Add metadata header
const header = `# n8n Security Audit Report

**Generated:** ${prepData.reportDate}  
**Risk Level:** ${aiOutput.riskLevel}  
**Report ID:** AUDIT-${Date.now()}

---

`;

reportContent = header + reportContent;

// Create report title for Google Doc
const reportTitle = `n8n Security Audit - ${new Date().toISOString().split('T')[0]} - ${aiOutput.riskLevel} Risk`;

return {
  json: {
    // Document metadata
    reportTitle,
    reportContent,

    // Full AI output preserved
    aiAnalysis: {
      executiveSummary: aiOutput.executiveSummary,
      riskLevel: aiOutput.riskLevel,
      criticalFindings: aiOutput.criticalFindings,
      immediateActions: aiOutput.immediateActions,
      shortTermActions: aiOutput.shortTermActions || [],
      fullReport: aiOutput.fullReport
    },

    // Raw metrics for downstream use
    metrics: prepData.summary.metrics,

    // Audit context
    auditData: {
      generatedAt: prepData.generatedAt,
      reportDate: prepData.reportDate,
      instanceSettings: prepData.summary.instanceSettings,
      unusedCredentialsList: prepData.summary.unusedCredentialsList,
      sqlIssuesList: prepData.summary.sqlIssuesList,
      communityPackages: prepData.summary.communityPackages,
      filesystemNodesList: prepData.summary.filesystemNodesList,
      riskyNodesByWorkflow: prepData.summary.riskyNodesByWorkflow
    }
  }
};

