{
  "type": "object",
  "properties": {
    "executiveSummary": {
      "type": "string",
      "description": "The executive summary section of the report"
    },
    "riskLevel": {
      "type": "string",
      "enum": ["LOW", "MEDIUM", "HIGH"],
      "description": "Overall risk level assessment"
    },
    "criticalFindings": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "title": { "type": "string" },
          "severity": { "type": "string" },
          "description": { "type": "string" },
          "remediation": { "type": "string" }
        }
      },
      "description": "List of critical findings"
    },
    "immediateActions": {
      "type": "array",
      "items": { "type": "string" },
      "description": "Actions to take this week"
    },
    "shortTermActions": {
      "type": "array",
      "items": { "type": "string" },
      "description": "Actions to take this month"
    },
    "fullReport": {
      "type": "string",
      "description": "The complete formatted report in markdown"
    }
  },
  "required": ["executiveSummary", "riskLevel", "criticalFindings", "immediateActions", "fullReport"]
}
