for (const item of $input.all()) {
  const tickets = item.json.tickets || [];

  const now = new Date();
  const thirtyDaysAgo = new Date(now - 30 * 24 * 60 * 60 * 1000);

  const recentTickets = tickets.filter(t => new Date(t.created_at) > thirtyDaysAgo);
  const openTickets = tickets.filter(t => t.status === 'open');

  item.json.ticket_count_last_30d = recentTickets.length;
  item.json.open_ticket_count = openTickets.length;
  item.json.avg_csatscore = tickets.length > 0 ? 
    tickets.reduce((sum, t) => sum + (t.csat_score || 0), 0) / tickets.length : 0;
  item.json.avg_first_response_time = tickets.length > 0 ?
    tickets.reduce((sum, t) => sum + (t.first_response_hours || 0), 0) / tickets.length : 0;
}

return $input.all();

