for (const item of $input.all()) {
  const events = item.json.events || [];

  item.json.email_opened = events.some(e => e.event === 'open');
  item.json.email_clicked = events.some(e => e.event === 'click');
  item.json.delivery_status = events.some(e => e.event === 'bounce') ? 'failed' : 'delivered';
  item.json.metrics_status = 'updated';
}

return $input.all();

// Ëß£Á¥Ñ„ÉªÁ∂ôÁ∂ö„Éª„Ç¢„ÉÉ„Éó„Ç∞„É¨„Éº„ÉâÂà§ÂÆö
for (const item of $input.all()) {
  const currentStatus = item.json.status;
  const currentMrr = item.json.current_mrr || 0;
  const originalMrr = item.json.mrr || 0;

  if (currentStatus === 'cancelled' || currentStatus === 'churned') {
    item.json.churned = true;
    item.json.retained = false;
    item.json.upgraded = false;
  } else if (currentMrr > originalMrr) {
    item.json.churned = false;
    item.json.retained = true;
    item.json.upgraded = true;
  } else {
    item.json.churned = false;
    item.json.retained = true;
    item.json.upgraded = false;
  }

  item.json.followup_status = 'done';
}

return $input.all();
