const content = $input.first().json.text;
// Extract JSON array from text
const match = content.match(/\[.*\]/s);
if (!match) throw new Error("No JSON array found");
const tags = JSON.parse(match[0]);

return tags.map(tag => ({ json: { tag: tag.replace('#', '') } }));

// Get data from previous nodes
// Cached items
const cachedItems = $input.all().map(i => i.json).filter(i => i.source === 'cache');

// Fresh items
let freshItems = [];
try {
    freshItems = $('Format New Hashtag Data').all().map(i => i.json);
} catch (e) {}

const allItems = [...cachedItems, ...freshItems];

// Sort by average_likes desc
allItems.sort((a, b) => (b.average_likes || 0) - (a.average_likes || 0));

// Pick Top 15 candidates for AI to choose from
const candidates = allItems.slice(0, 15).map(i => ({
    tag: i.tag || i.name,
    likes: i.average_likes || 0,
    comments: i.average_comments || 0
}));

// Get original caption for context. Handle both manual trigger and workflow trigger paths.
let caption = '';
try {
    caption = $('Set Dummy Caption').first().json.caption;
} catch(e) {}

if (!caption) {
    try {
        caption = $('Workflow Trigger').first().json.caption;
    } catch(e) {}
}

return { json: { caption, candidates } };

const allItems = $input.all();
const sheetRows = allItems.filter(i => i.json.tag && (i.json.average_likes !== undefined || i.json.media_count !== undefined)).map(i => i.json);
const aiTags = allItems.filter(i => !i.json.average_likes && !i.json.media_count && i.json.tag).map(i => i.json.tag);

const sheetMap = new Map();
sheetRows.forEach(row => {
    if(row.tag) {
        // Convert to number if possible, undefined only if empty/invalid
        const likes = row.average_likes !== '' && row.average_likes !== null ? Number(row.average_likes) : undefined;
        const comments = row.average_comments !== '' && row.average_comments !== null ? Number(row.average_comments) : undefined;
        const media = row.media_count !== '' && row.media_count !== null ? Number(row.media_count) : undefined;

        // Normalize cache keys to lowercase
        const cleanTag = row.tag.replace('#','').toLowerCase();
        sheetMap.set(cleanTag, {
            average_likes: !isNaN(likes) ? likes : undefined,
            average_comments: !isNaN(comments) ? comments : undefined,
            media_count: !isNaN(media) ? media : undefined
        });
    }
});

const results = [];
aiTags.forEach(tag => {
    // Normalize input to lowercase
    const cleanTag = tag.replace('#','').toLowerCase();
    const cache = sheetMap.get(cleanTag);
    // Only treat as cache if we have the new metric (average_likes) as a valid number
    if (cache && typeof cache.average_likes === 'number') {
        results.push({ json: { 
            tag: cleanTag, 
            ...cache,
            source: 'cache' 
        }});
    } else {
        results.push({ json: { tag: cleanTag, source: 'new' } });
    }
});

return results;

// Get input data (Facebook API node "data" array)
const items = $input.all()[0].json.data;

if (!items || items.length === 0) {
  return { average_likes: 0, average_comments: 0 };
}

const totalPosts = items.length;
const totalLikes = items.reduce((sum, post) => sum + (post.like_count || 0), 0);
const totalComments = items.reduce((sum, post) => sum + (post.comments_count || 0), 0);

// Output specific format
return {
  average_likes: Math.round(totalLikes / totalPosts),
  average_comments: Math.round(totalComments / totalPosts)
};
