// CONFIG values
const preferredRemote = $node["Config"].json.preferredRemote || "";
const preferredEasyApply = $node["Config"].json.preferredEasyApply || "";
const maxJobs = $node["Config"].json.maxJobsPerSearch || 10;

// Get AI Raw Output
if (!items.length || !items[0].json.output) {
  throw new Error('Resume Analyzer Agent returned no data.');
}

let raw = items[0].json.output.toString().trim();

raw = raw
  .replace(/^```json/i, '')
  .replace(/^```/, '')
  .replace(/```$/, '')
  .trim();

let data;
try {
  data = JSON.parse(raw);
} catch (error) {
  throw new Error('JSON parse error: ' + error.message + "\nRaw: " + raw);
}

const searches = data.searches || [];

// MAP OUTPUT + APPLY CONFIG PREFERENCES
return searches.map(s => ({
  json: {
    Keyword: s.keyword || '',
    Location: s.location || '',
    'Experience Level': s.experience_level || '',

    // Apply Preferred Remote if config set else use AI
    Remote: preferredRemote !== "" ? preferredRemote : (s.remote || ''),

    // Apply Preferred Easy Apply if config set else use AI
    'Easy Apply': preferredEasyApply !== "" ? preferredEasyApply : (s.easy_apply || ''),

    // Pass max jobs forward to next nodes
    maxJobsPerSearch: maxJobs
  }
}));

const limit = $node["Config"].json.maxJobsPerSearch || 10;

// "items" is the array of job items coming into this node
return items.slice(0, limit);
