// Calculate analysis period
const now = new Date();
const yesterday = new Date(now);
yesterday.setDate(yesterday.getDate() - 1);

// Set from 00:00:00 to 23:59:59 of the previous day
const analysisStart = new Date(yesterday);
analysisStart.setHours(0, 0, 0, 0);

const analysisEnd = new Date(yesterday);
analysisEnd.setHours(23, 59, 59, 999);

return {
  analysisStart: analysisStart.toISOString(),
  analysisEnd: analysisEnd.toISOString(),
  analysisStartUnix: Math.floor(analysisStart.getTime() / 1000),
  analysisEndUnix: Math.floor(analysisEnd.getTime() / 1000)
};

// Combine messages for summary
const messages = $input.item.json.messages || [];
const maxMessages = 50; // Up to the latest 50 messages

const messageTexts = messages
  .slice(0, maxMessages)
  .map(m => m.text)
  .filter(text => text && text.length > 0)
  .join('\n');

return {
  ...$input.item.json,
  messageSummary: messageTexts,
  messageCount: messages.length
};

// Calculate attendance metrics
const logs = $input.item.json.attendance_logs || [];

let totalWorkHours = 0;
let totalOvertimeHours = 0;
let lateArrivalCount = 0;
let absentCount = 0;

logs.forEach(log => {
  totalWorkHours += log.work_hours || 0;
  totalOvertimeHours += log.overtime_hours || 0;
  if (log.late_arrival) lateArrivalCount++;
  if (log.absent) absentCount++;
});

const avgWorkHours = logs.length > 0 ? totalWorkHours / logs.length : 0;

return {
  ...$input.item.json,
  avg_work_hours: avgWorkHours,
  total_overtime_hours: totalOvertimeHours,
  late_arrival_count: lateArrivalCount,
  absent_count: absentCount
};

// Calculate task metrics
const tasks = $input.item.json.tasks || [];

let completedCount = 0;
let overdueCount = 0;
let totalDelayDays = 0;

tasks.forEach(task => {
  if (task.status === 'completed') completedCount++;
  if (task.overdue) {
    overdueCount++;
    totalDelayDays += task.delay_days || 0;
  }
});

const completionRate = tasks.length > 0 ? completedCount / tasks.length : 0;
const avgDelayDays = overdueCount > 0 ? totalDelayDays / overdueCount : 0;

return {
  ...$input.item.json,
  task_completion_rate: completionRate,
  overdue_task_count: overdueCount,
  avg_task_delay_days: avgDelayDays
};

// Generate anonymized data
const crypto = require('crypto');

// Hash employee_id
const hash = crypto.createHash('sha256');
hash.update($input.item.json.employee_id);
const employee_hash = hash.digest('hex');

const stressData = JSON.parse($input.item.json.response || '{}');

return {
  employee_hash: employee_hash,
  employee_id: $input.item.json.employee_id, // Keep for separate branch
  slack_user_id: $input.item.json.slack_user_id, // Keep for separate branch
  department: $input.item.json.department,
  stress_score: stressData.stress_score || 0,
  stress_level: stressData.stress_level || 'unknown',
  main_risk_factors: stressData.main_risk_factors || [],
  analysis_date: new Date().toISOString().split('T')[0],
  analysis_period_from: $input.item.json.analysisStart,
  analysis_period_to: $input.item.json.analysisEnd
};
