parameters.jsCode:
const now = new Date();
const out = [];

for (const item of items) {
  const deal = item.json;

  // Inactivity
  let inactivity = deal.Last_Activity_Time
    ? Math.floor((now - new Date(deal.Last_Activity_Time)) / (1000*60*60*24))
    : null;

  // Stage age
  let stageStart = deal.Stage_Span || deal.Stage_Start_Time || null;
  let stage_days = stageStart
    ? Math.floor((now - new Date(stageStart)) / (1000*60*60*24))
    : null;

  // Deal age
  let deal_age = deal.Created_Time
    ? Math.floor((now - new Date(deal.Created_Time)) / (1000*60*60*24))
    : null;

  out.push({
    json: {
      ...deal,
      inactivity_days: inactivity,
      stage_days,
      deal_age
    }
  });
}

return out;

