// ----------------------
// AI-style Dynamic Forecast Function Node
// ----------------------

// Get all items from the Merge node
const allItems = $input.all();

//  Separate current deals and historical deals
// Assuming your "Get Deals" node fetches both historical and current deals
// You can adjust historical vs current by date if needed
const deals = allItems
    .map(item => item.json)
    .filter(d => d.Amount && d.Probability); // basic filter

//  Get Market Signal
const marketItem = allItems.find(item => item.json.marketSignal !== undefined);
const marketSignal = marketItem?.json?.marketSignal || 1;

//  Helper function to safely parse numbers
function safeNumber(value, fallback = 0) {
    if (value === null || value === undefined || value === "") return fallback;
    const num = parseFloat(value);
    return isNaN(num) ? fallback : num;
}

// ----------------------
//  Dynamic Seasonality Calculation
// Using historical deal close dates to compute seasonal factors
// ----------------------
const historicalDeals = deals.filter(d => d.Close_Date); // all deals with close date
const monthlyTotals = Array(12).fill(0);
const monthlyCounts = Array(12).fill(0);

historicalDeals.forEach(d => {
    const closeMonth = new Date(d.Close_Date).getMonth(); // 0 = Jan
    const amount = safeNumber(d.Amount);
    const probability = safeNumber(d.Probability) / 100;
    monthlyTotals[closeMonth] += amount * probability;
    monthlyCounts[closeMonth] += 1;
});

// Normalize to average
const totalRevenue = monthlyTotals.reduce((a, b) => a + b, 0);
const avgRevenue = totalRevenue / 12;
const seasonalityFactors = monthlyTotals.map(total => (avgRevenue ? total / avgRevenue : 1));

// Current month factor
const currentMonth = new Date().getMonth();
const seasonalFactor = seasonalityFactors[currentMonth] || 1;

// ----------------------
//  Filter valid deals (Amount > 0 and Probability > 0)
// ----------------------
const validDeals = deals.filter(d => safeNumber(d.Amount) > 0 && safeNumber(d.Probability) > 0);

// ----------------------
//  Compute Forecast with Market + Seasonal Factors
// ----------------------
return validDeals.map(d => {
    const dealName = d.Deal_Name || "Unnamed Deal";
    const amount = safeNumber(d.Amount);
    const probability = safeNumber(d.Probability) / 100;

    const pipelineWeighted = amount * probability;
    const expectedRevenue = safeNumber(d.Expected_Revenue, pipelineWeighted);

    // AI-style adjusted forecast
    const adjustedForecast = pipelineWeighted * marketSignal * seasonalFactor;

    return {
        json: {
            id: d.id,
            Deal_Name: dealName,
            Stage: d.Stage,
            Amount: amount,
            Probability: probability,
            Expected_Revenue: expectedRevenue,
            Market_Signal: marketSignal,
            Seasonal_Factor: seasonalFactor,
            Market_Seasonal_Adjusted_Forecast: adjustedForecast
        }
    };
});

