const config = $('‚öôÔ∏è Config').first().json;
const formData = $input.first().json;

const extractedData = {
  companyName: formData["What's your company name?"] || '',
  websiteUrl: formData["What's your company website?"] || '',
  businessDescription: formData["Tell us about your business"] || '',
  industry: formData["What industry are you in?"] || '',
  currentProblem: formData["What's your biggest operational challenge?"] || '',
  desiredSolution: formData["What would the ideal solution look like?"] || '',
  platformsToUse: formData["What tools/platforms do you currently use?"] || '',
  projectComplexity: formData["How complex is this project?"] || '',
  clientFirstName: formData["First name"] || '',
  clientLastName: formData["Last name"] || '',
  clientEmail: formData["Email address"] || '',
  additionalNotes: formData["Anything else we should know?"] || ''
};

// Parse budget range to number
function parseBudget(label) {
  const l = (label || '').toLowerCase();
  if (l.includes('under') && l.includes('1,500')) return 1250;
  if (l.includes('1,500') && l.includes('2,500')) return 2000;
  if (l.includes('2,500') && l.includes('5,000')) return 3750;
  if (l.includes('5,000') && l.includes('10,000')) return 7500;
  if (l.includes('10,000+')) return 12500;
  return 3000;
}

extractedData.budgetLabel = formData["What's your estimated budget?"] || '';
extractedData.estimatedValue = parseBudget(extractedData.budgetLabel);

// Template selection logic
const threshold = Number(config.quickQuoteThreshold) || 2500;
const isComplex = (extractedData.projectComplexity || '').toLowerCase().includes('complex');

extractedData.selectedTemplate = (extractedData.estimatedValue < threshold && !isComplex) ? 'quick_quote' : 'standard';
extractedData.templateReason = extractedData.selectedTemplate === 'quick_quote' 
  ? `Budget $${extractedData.estimatedValue} < $${threshold}` 
  : `Budget $${extractedData.estimatedValue} >= $${threshold} or complex project`;
extractedData.pandaDocTemplateId = extractedData.selectedTemplate === 'quick_quote' 
  ? config.quickQuoteTemplateId 
  : config.standardProposalTemplateId;

// Calculate amounts
extractedData.depositAmount = Math.round(extractedData.estimatedValue * 0.5);
extractedData.balanceAmount = extractedData.estimatedValue - extractedData.depositAmount;

// Add dates
const today = new Date();
const validityDays = extractedData.selectedTemplate === 'quick_quote' ? 7 : 14;
const expiryDate = new Date(today.getTime() + validityDays * 24 * 60 * 60 * 1000);
extractedData.documentDate = today.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
extractedData.proposalExpiryDate = expiryDate.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

extractedData.config = config;
extractedData.caseStudy = config.caseStudies?.default || {};

return [{ json: extractedData }];

