// Parse GPT response and merge with extracted data
const raw = $input.first().json;
const extractedData = $('üîÑ Extract & Transform Data').first().json;

let content = raw.message?.content ?? raw.content ?? raw;

try {
  if (typeof content === 'string') {
    // Remove markdown code blocks if present
    content = content.replace(/```json\s*/g, '').replace(/```\s*/g, '').trim();
    content = JSON.parse(content);
  }

  // Merge GPT response with extracted form data
  return [{ 
    json: { 
      ...extractedData,
      ...content, 
      proposalParseOk: true 
    } 
  }];
} catch (e) {
  return [{ 
    json: { 
      ...extractedData,
      proposalParseOk: false, 
      proposalParseError: e.message 
    } 
  }];
}

