// Calculate attendance status
const participant = $input.item.json;
const meetingDuration = $('Get Meeting Details').item.json.duration || 60; // in minutes
const participantDuration = participant.duration || 0; // in minutes

// Determine attendance status
let status = 'attended';
let sendFollowUp = false;

if (participantDuration === 0) {
  status = 'no-show';
  sendFollowUp = true;
} else if (participantDuration < (meetingDuration * 0.5)) {
  status = 'early-leaver';
  sendFollowUp = true;
} else if (participantDuration < (meetingDuration * 0.8)) {
  status = 'partial-attendance';
  sendFollowUp = true;
}

return {
  ...participant,
  attendanceStatus: status,
  sendFollowUp: sendFollowUp,
  meetingTopic: $('Get Meeting Details').item.json.topic,
  meetingId: $('Get Meeting Details').item.json.id,
  meetingDuration: meetingDuration,
  attendancePercentage: Math.round((participantDuration / meetingDuration) * 100)
};

// Get next session registration link
// This could come from a database or calendar integration
// For now, using a placeholder that should be replaced

const nextSessionLink = 'https://zoom.us/meeting/register/YOUR_NEXT_SESSION_ID';

// You could also fetch this from a Google Sheet or database
// Example: Query upcoming events from a calendar or registration system

return {
  ...($('Evaluate Attendance').item.json),
  recordingUrl: $('Get Recording Link').item.json.share_url || $('Get Recording Link').item.json.recording_files?.[0]?.download_url || 'Recording processing',
  handoutUrl: $('Get Handout Materials').item.json.webContentLink || 'Materials URL',
  nextSessionLink: nextSessionLink,
  emailSubject: `Follow-up: ${$('Evaluate Attendance').item.json.meetingTopic} - ${$('Evaluate Attendance').item.json.attendanceStatus === 'no-show' ? 'Recording Available' : 'Complete Recording Available'}`,
};
