const emailRegex = /[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}/;
const input = items;
const source = input[0] && input[0].json ? input[0].json : input[0];
const records = (source && source.result && Array.isArray(source.result.records))
  ? source.result.records
  : [];

const output = records.map(rec => {
  const tryFindEmail = (text) => {
    if (!text || typeof text !== 'string') return null;
    const m = text.match(emailRegex);
    return m ? m[0] : null;
  };

  let found = tryFindEmail(rec.name);
  if (!found) found = tryFindEmail(rec.reason);

  if (!found) {
    return { json: { id: rec.id, result: "Email not found" } };
  }

  const local = found.split('@')[0];
  const beforeFirstDot = local.split('.')[0];
  return { json: { id: rec.id, rawEmail: found, extracted: beforeFirstDot, login: local } };
});

return output;

