// items = array of users with presence and member id + additional data
const users = items.map(item => item.json);

// Filter active users
const activeUsers = users.filter(u => u.presence === 'active');

let selectedUser;

// Case 1 & 2: If any active user exists
if (activeUsers.length > 0) {
  // Pick random active user
  const randomIndex = Math.floor(Math.random() * activeUsers.length);
  selectedUser = activeUsers[randomIndex];
} else {
  // Case 3: No active users, pick first user as fallback
  selectedUser = users[0];
}

// Return all details for selected user
return [{ json: selectedUser }];
