// Collect URLs from Decodo Google Search output
const allItems = $input.all();

const urls = [];

function collectUrls(obj) {
  if (!obj || typeof obj !== 'object') return;

  // Collect URL fields
  if (typeof obj.url === 'string' && obj.url.startsWith('http')) {
    urls.push(obj.url);
  }

  // Recurse into arrays
  if (Array.isArray(obj)) {
    for (const item of obj) {
      collectUrls(item);
    }
  }
  // Recurse into objects
  else {
    for (const key of Object.keys(obj)) {
      collectUrls(obj[key]);
    }
  }
}

// Walk through every Decodo item
for (const item of allItems) {
  collectUrls(item.json);
}

// Deduplicate + remove Google search URLs
const uniqueUrls = [...new Set(urls)]
  .filter(url => !url.includes('google.com/search'))
  .slice(0, 20); // ‚úÖ LIMIT TO 20 ONLY

// Return one item per URL
return uniqueUrls.map(url => ({
  json: { url }
}));

const item = $input.item.json;

let html = "";

try {
  html = item.results[0].content || "";
} catch (e) {
  html = "";
}

let cleaned = html
  .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, "")
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "")
  .replace(/<noscript[^>]*>[\s\S]*?<\/noscript>/gi, "")
  .replace(/<\/?[^>]+>/g, " ")
  .replace(/&nbsp;/gi, " ")
  .replace(/&amp;/gi, "&")
  .replace(/&quot;/gi, '"')
  .replace(/&#39;/gi, "'")
  .replace(/&lt;/gi, "<")
  .replace(/&gt;/gi, ">")
  .replace(/\s+/g, " ")
  .trim();

return {
  json: {
    text_clean: cleaned,
    url: $('Loop (Amazon)').first().json.url
  }
};

