  $('Telegram Trigger').first().json.message.text
    .toLowerCase()
    .replace(/[^\w\s\u0600-\u06FF]/g, ' ');

// 2Ô∏è‚É£ Tokenize user message dynamically
const userTokens = userMessage
  .split(/\s+/)
  .filter(w => w.length >= 3);

// 3Ô∏è‚É£ Cleaned page text
const pageText = ($json.text_clean || '')
  .toLowerCase()
  .replace(/[^\w\s\u0600-\u06FF]/g, ' ');

// 4Ô∏è‚É£ URL (pass-through from earlier nodes)
const url = $json.url || null;

// Guards
if (!pageText || userTokens.length === 0) {
  return [{
    json: {
      match: false,
      score: 0,
      url
    }
  }];
}

// 5Ô∏è‚É£ Dynamic scoring
let score = 0;
let matchedTokens = [];

for (const token of userTokens) {
  const occurrences = pageText.split(token).length - 1;
  if (occurrences > 0) {
    score += Math.min(occurrences * 3, 15); // density-aware
    matchedTokens.push(token);
  }
}

// 6Ô∏è‚É£ Structural bonuses (language-agnostic)
if (pageText.length > 1500) score += 5;   // real product page
if (pageText.length < 400) score -= 10;   // weak page
if (
  pageText.includes('add to cart') ||
  pageText.includes('ÿßÿ∂ŸÅ') ||
  pageText.includes('ÿ£ÿ∂ŸÅ')
) score += 5;

// 7Ô∏è‚É£ Dynamic threshold (based on user input length)
const threshold = Math.max(12, userTokens.length * 4);
const isMatch = score >= threshold;

// 8Ô∏è‚É£ Output EVERYTHING needed downstream
return [{
  json: {
    match: isMatch,
    score,
    threshold,
    url,
    matched_tokens: matchedTokens,
