parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"propertyId": {
			"type": "string",
			"description": "Unique identifier for the property"
		},
		"propertyName": {
			"type": "string",
			"description": "Name of the property"
		},
		"overallRiskScore": {
			"type": "number",
			"description": "Overall risk score from 0-100"
		},
		"financialRisk": {
			"type": "number",
			"description": "Financial risk score from 0-100"
		},
		"operationalRisk": {
			"type": "number",
			"description": "Operational risk score from 0-100"
		},
		"legalRisk": {
			"type": "number",
			"description": "Legal risk score from 0-100"
		},
		"insuranceRisk": {
			"type": "number",
			"description": "Insurance risk score from 0-100"
		},
		"regulatoryRisk": {
			"type": "number",
			"description": "Regulatory compliance risk score from 0-100"
		},
		"complianceIssues": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "List of identified compliance issues"
		},
		"recommendations": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "Recommended actions to mitigate risks"
		}
	}
}

parameters.jsCode:
const items = $input.all();
const correctionActions = [];

for (const item of items) {
  const data = item.json;

  const action = {
    propertyId: data.propertyId,
    propertyName: data.propertyName,
    overallRiskScore: data.overallRiskScore,
    urgency: data.overallRiskScore >= 80 ? 'critical' : 'high',
    complianceIssues: data.complianceIssues || [],
    recommendations: data.recommendations || [],
    timestamp: new Date().toISOString(),
    actionType: 'risk_mitigation'
  };

  correctionActions.push({ json: action });
}

return correctionActions;

