function cleanAndExtractJSON(response) {
    try {
        const result = {
            image_prompt: []
        };

        const lines = response.split('\n');
        let currentPrompt = '';

        for (const line of lines) {
            if (line.includes('"prompt":')) {
                if (currentPrompt) {
                    result.image_prompt.push(currentPrompt.trim());
                }
                currentPrompt = line.split('"prompt":')[1].trim();
            }
        }

        if (currentPrompt) {
            result.image_prompt.push(currentPrompt.trim());
        }

        return { json: result };

    } catch (error) {
        return { 
            json: {
                image_prompt: []
            }
        };
    }
}

const response = $input.first().json.output;
return cleanAndExtractJSON(response);

