const items = $input.all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // Cek jika HTTP request berhasil
  if (item.json && item.json.url) {
    results.push({
      json: {
        imageUrl: item.json.url,
        fileName: item.json.fileName || `image_${i + 1}.png`,
        success: true
      }
    });
  } else {
    // Jika gagal, buat URL langsung dari Pollinations
    const prompt = $('Code - Get Prompt').item.json.body.prompt;
    const width = $('Fields - Set Values').item.json.width;
    const height = $('Fields - Set Values').item.json.height;

    const directUrl = `https://image.pollinations.ai/prompt/${encodeURIComponent(prompt)}?width=${width}&height=${height}&model=flux&seed=42&nologo=true`;

    results.push({
      json: {
        imageUrl: directUrl,
        fileName: `image_${i + 1}.png`,
        success: true
      }
    });
  }
}

return results;

