// Hole die Pricing-Daten vom Filter Node
const modelData = $('Filter').first().json;
const pricing = modelData.pricing;

// Hole die Token-Usage vom LangChain Code Node
const tokenUsage = $json.tokenUsage;

// Preise pro Token (von OpenRouter - sind bereits in Dollar pro Token)
const promptPrice = parseFloat(pricing.prompt);      // z.B. 0.0000004
const completionPrice = parseFloat(pricing.completion); // z.B. 0.0000016

// Berechne Kosten
const inputCost = tokenUsage.input_tokens * promptPrice;
const outputCost = tokenUsage.output_tokens * completionPrice;
const totalCost = inputCost + outputCost;

return {
  // Original Output
  output: $json.output,

  // Token Usage
  tokenUsage: {
    input_tokens: tokenUsage.input_tokens,
    output_tokens: tokenUsage.output_tokens,
    total_tokens: tokenUsage.total_tokens
  },

  // Kosten in USD
  costs: {
    input_cost_usd: inputCost,
    output_cost_usd: outputCost,
    total_cost_usd: totalCost,
    // Formatiert f√ºr bessere Lesbarkeit
    total_cost_formatted: `$${totalCost.toFixed(8)}`
  },

  // Model Info
  model: {
    id: modelData.id,
    name: modelData.name,
    prompt_price_per_token: promptPrice,
    completion_price_per_token: completionPrice
  }
};

