const { PromptTemplate } = require('@langchain/core/prompts');

// Get the LLM from ai_languageModel input
const llm = await this.getInputConnectionData('ai_languageModel', 0);

// Get the chat input
const chatInput = $('When Executed by Another Workflow').first().json.chatInput;

// Variable for token data
let tokenUsage = null;

// Callback handler for token tracking
const tokenCallback = {
  handleLLMEnd: async function(output, runId, parentId) {
    try {
      const generation = output.generations[0][0];
      const message = generation.message;
      if (message && message.usage_metadata) {
        tokenUsage = {
          input_tokens: message.usage_metadata.input_tokens,
          output_tokens: message.usage_metadata.output_tokens,
          total_tokens: message.usage_metadata.total_tokens
        };
      }
    } catch (e) {
      console.log('Token tracking error:', e);
    }
  }
};

// Create prompt and chain
const prompt = PromptTemplate.fromTemplate('{input}');
const chain = prompt.pipe(llm);

// Invoke WITH callbacks (important: on invoke, not on the LLM!)
const response = await chain.invoke(
  { input: chatInput },
  { callbacks: [tokenCallback] }
);

// Return response with token data
return [{
  json: {
    output: response.content,
    tokenUsage: tokenUsage
  }
}];

