// Dropdown values must be { option: "..." } format (not name/value)
const dropdownValues = items
  .map(item => ({
    option: item.json.id,
  }))
  .sort((a, b) => a.option.localeCompare(b.option));

const formJson = [
  {
    fieldLabel: "Select Model",
    fieldType: "dropdown",
    fieldOptions: {
      values: dropdownValues,
    },
    requiredField: true,
  },
  {
    fieldLabel: "Prompt",
    fieldType: "textarea",
    placeholder: "Enter your prompt/question",
    requiredField: true,
  },
];

// Form Node expects a single item carrying the JSON
return [{
  json: {
    formJson,
    // Pass templates through for resolution after form submit
    templates: items.map(i => i.json),
  }
}];

