    .output a {
      color: #60a5fa;
      text-decoration: none;
    }
    .output a:hover { text-decoration: underline; }

    /* Meta & Details */
    .meta {
      display: flex;
      justify-content: space-between;
      align-items: center;
      font-size: 0.875rem;
      margin-bottom: 1rem;
    }
