      color: #22c55e;
      font-family: monospace;
      font-size: 0.9rem;
    }

    details {
      border-top: 1px solid #2a2a2a;
      padding-top: 1rem;
    }
    summary {
      cursor: pointer;
      color: #888;
      font-size: 0.8rem;
      user-select: none;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }
    summary:hover { color: #aaa; }
    summary::marker { content: ''; }
    summary .icon {
      transition: transform 0.2s;
      font-size: 0.7rem;
    }
    details[open] summary .icon {
      transform: rotate(90deg);
    }

    .details-grid {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 1rem;
      margin-top: 1rem;
      padding: 1rem;
      background: #141414;
      border-radius: 8px;
    }
    .detail-section h4 {
      color: #666;
      font-size: 0.7rem;
      text-transform: uppercase;
      letter-spacing: 0.05em;
      margin-bottom: 0.5rem;
    }
    .detail-row {
      display: flex;
      justify-content: space-between;
      font-size: 0.8rem;
      padding: 0.25rem 0;
    }
    .detail-row .label { color: #888; }
    .detail-row .value { 
      font-family: monospace;
      color: #e5e5e5;
    }
    .detail-row .value.green { color: #22c55e; }
    .detail-row .value.blue { color: #60a5fa; }
  </style>
