            <h4>Costs</h4>
            <div class="detail-row">
              <span class="label">Input</span>
              <span class="value">${{ $json.costs.input_cost_usd }}</span>
            </div>
            <div class="detail-row">
              <span class="label">Output</span>
              <span class="value">${{ $json.costs.output_cost_usd }}</span>
            </div>
            <div class="detail-row">
              <span class="label">Total</span>
              <span class="value green">${{ $json.costs.total_cost_usd }}</span>
            </div>
          </div>

          <div class="detail-section" style="grid-column: span 2;">
            <h4>Model Pricing</h4>
            <div class="detail-row">
              <span class="label">Prompt (per token)</span>
              <span class="value">${{ $json.model.prompt_price_per_token }}</span>
            </div>
            <div class="detail-row">
              <span class="label">Completion (per token)</span>
              <span class="value">${{ $json.model.completion_price_per_token }}</span>
            </div>
            <div class="detail-row">
              <span class="label">Model Name</span>
              <span class="value">{{ $json.model.name }}</span>
            </div>
          </div>
        </div>
      </details>
    </div>
  </div>
</body>
</html>

