const output = [];

for (const item of items) {
  const data = item.json;

  const googleAiModeUrl = data.search_metadata?.google_ai_mode_url || null;
  const references = data.references || [];
  const textBlocks = data.text_blocks || [];

  // 1Ô∏è‚É£ Parse text_blocks (paragraphs + lists)
  for (const block of textBlocks) {
    // Paragraphs
    if (block.type === "paragraph" && block.snippet) {
      output.push({
        source: "SerpAPI AI Mode",
        site: "Aggregated",
        title: "Summary Insight",
        text: block.snippet,
        url: null,
        google_ai_mode_url: googleAiModeUrl,
        category: "overview",
        is_reddit: false
      });
    }

    // Lists (complaints / praise / signals)
    if (block.type === "list" && Array.isArray(block.list)) {
      for (const entry of block.list) {
        if (entry.snippet) {
          output.push({
            source: "SerpAPI AI Mode",
            site: "Aggregated",
            title: "Key Point",
            text: entry.snippet,
            url: null,
            google_ai_mode_url: googleAiModeUrl,
            category: "key_insight",
            is_reddit: false
          });
        }
      }
    }
  }

  // 2Ô∏è‚É£ Parse references (site-wise sentiment sources)
  for (const ref of references) {
    const site =
      ref.source?.toLowerCase().includes("reddit")
        ? "Reddit"
        : ref.source?.toLowerCase().includes("glassdoor")
        ? "Glassdoor"
        : ref.source?.toLowerCase().includes("ambitionbox")
        ? "AmbitionBox"
        : ref.source?.toLowerCase().includes("clutch")
        ? "Clutch"
        : "Other";

    output.push({
      source: "Reference",
      site,
      title: ref.title || "Untitled",
      text: ref.snippet || "",
      url: ref.link || null,
      google_ai_mode_url: googleAiModeUrl,
      category:
        site === "Reddit"
          ? "public_discussion"
          : site === "Clutch"
          ? "client_review"
          : "employee_review",
      is_reddit: site === "Reddit"
    });
  }
}

// 3Ô∏è‚É£ Return each insight as a separate n8n item
return output.map(item => ({ json: item }));

const cleanedOutput = [];

for (const item of items) {
  const raw = item.json.output;

  if (!raw || typeof raw !== "string") {
    continue;
  }

  try {
    const parsed = JSON.parse(raw);

    cleanedOutput.push({
      sentiment: parsed.sentiment || "Unknown",
      risk_detected: Boolean(parsed.risk_detected),
      risk_level: parsed.risk_level || "Low",
      reason: parsed.reason || "",
      source_platform: parsed.source_platform || "Other",
      immediate_action_required: Boolean(parsed.immediate_action_required)
    });

  } catch (error) {
    // Fail-safe for malformed JSON
    cleanedOutput.push({
      sentiment: "Unknown",
      risk_detected: false,
      risk_level: "Low",
      reason: "Failed to parse AI response",
      source_platform: "Other",
      immediate_action_required: false
    });
  }
}

// Return clean items (one object per insight)
return cleanedOutput.map(obj => ({ json: obj }));
