const item = $input.first().json;
const lineItem = item.lineItems;

const quantity = lineItem.quantity || 1;
const rate = lineItem.rate || 0;
const lineTotal = quantity * rate;

return [{
  json: {
    invoiceId: item.invoiceId,
    clientName: item.clientName,
    clientEmail: item.clientEmail,
    taxRate: item.taxRate,
    dueDate: item.dueDate,
    lineItem: {
      description: lineItem.description,
      quantity: quantity,
      rate: rate,
      lineTotal: lineTotal
    }
  }
}];

const data = $input.first().json;
const lines = data.processedLines || [];

const invoiceId = lines[0]?.invoiceId || 'unknown';
const clientName = lines[0]?.clientName || '';
const clientEmail = lines[0]?.clientEmail || '';
const taxRate = lines[0]?.taxRate || 0.1;
const dueDate = lines[0]?.dueDate || '';

const subtotal = lines.reduce((sum, l) => sum + (l.lineItem?.lineTotal || 0), 0);
const tax = Math.round(subtotal * taxRate * 100) / 100;
const total = subtotal + tax;

const paymentLink = `https://pay.company.com/invoice/${invoiceId}`;

return [{
  json: {
    invoiceId: invoiceId,
    clientName: clientName,
    clientEmail: clientEmail,
    lineItems: lines.map(l => l.lineItem),
    subtotal: subtotal,
    taxRate: taxRate,
    tax: tax,
    total: total,
    dueDate: dueDate,
    paymentLink: paymentLink,
    status: 'pending',
    createdAt: new Date().toISOString()
  }
}];

