parameters.jsCode:
const invoices = $input.all();
const today = new Date();

const overdueInvoices = invoices.map(inv => {
  const dueDate = new Date(inv.json['Due Date']);
  const daysOverdue = Math.floor((today - dueDate) / (1000 * 60 * 60 * 24));

  let reminderLevel = 'none';
  if (daysOverdue >= 60) reminderLevel = 'collections';
  else if (daysOverdue >= 31) reminderLevel = 'final';
  else if (daysOverdue >= 15) reminderLevel = 'urgent';
  else if (daysOverdue >= 8) reminderLevel = 'second';
  else if (daysOverdue >= 1) reminderLevel = 'first';

  return {
    json: {
      invoiceId: inv.json['Invoice ID'],
      clientName: inv.json['Client'],
      clientEmail: inv.json['Email'],
      total: parseFloat(inv.json['Total']) || 0,
      dueDate: inv.json['Due Date'],
      daysOverdue: daysOverdue,
      reminderLevel: reminderLevel
    }
  };
}).filter(inv => inv.json.daysOverdue > 0);

return overdueInvoices;

