const data = $input.first().json;

// --- PRODUCT CATEGORY & TYPE MAPPING ---
const categoryMap = {
  "Necklace": {
    gCat: "Apparel & Accessories > Jewelry > Necklaces",
    type: "Necklaces",
    tags: ["Jewellery", "All Jewellery", "necklaces", "Categories_Necklaces"]
  },
  "Earrings": {
    gCat: "Apparel & Accessories > Jewelry > Earrings",
    type: "Earrings",
    tags: ["Jewellery", "All Jewellery", "Earrings", "Categories_Earrings"]
  },
  "Bangles": {
    gCat: "Apparel & Accessories > Jewelry > Bracelets",
    type: "Bangles",
    tags: ["Jewellery", "All Jewellery","bangles All","AD bangles","Bangle Sets"]
  },
  "Rings": {
    gCat: "Apparel & Accessories > Jewelry > Rings",
    type: "Rings",
    tags: ["Jewellery", "All Jewellery", "Rings", "Categories_Rings"]
  },
  "Combo": {
    gCat: "Apparel & Accessories > Jewelry > Jewelry Sets",
    type: "Combo",
    tags: ["Jewellery", "All Jewellery","combo","Categories_Combo"]
  },
  "Pendant": {
    gCat: "Apparel & Accessories > Jewelry",
    type: "Pendants",
    tags: ["Jewellery", "All Jewellery","pendants","Categories_Pendants"]
  }
};

// fallback if category not mapped
const cat = categoryMap[data.category] || {
  gCat: "",
  type: data.category,
  tags: ["Jewellery","All Jewellery"]
};

// -----------------------------------------------------------
// SMART TAG ENRICHMENT (Partial Keyword Matching from product_name & input tags)
// -----------------------------------------------------------
const keywordTags = [
  ["kund", "Kundan Sets"],
  ["haram", "Haram"],
  ["thread", "Thread Necklace"],
  ["temple", "Temple Necklaces"],
  ["stud", "Studs"],
  ["jhum", "Jhumkis"],
  ["dangle", "Dangler"],
  ["chand", "chandbali"],
  ["ad", "AD Necklaces"],
];

let autoTags = [];

const sourceText = `${data.product_name} ${data.tags}`.toLowerCase();
keywordTags.forEach(([key, tag]) => {
  if (sourceText.includes(key)) autoTags.push(tag);
});

// -----------------------------------------------------------
// FINAL FIELD BUILDING
// -----------------------------------------------------------
const title = `${data.vendor_name} ${data.product_name}`.trim();
const handle = `${data.vendor_name} ${data.product_name} ${data.sku}`.trim();

return {
  handle,
  title,
  vendor: data.vendor_name,
  product_category: cat.gCat,
  type: cat.type,
  tags: [data.vendor_name, data.tags, ...cat.tags, ...autoTags].join(", "),
  color: data.combine_color,
  image_alt_text: title,
  seo_title: title,
};

const d = $('get finalized data').first().json;

const html =
`<h1>${d.product_name}</h1>
<p>${d.ai_description}</p>
<p><strong>Ideal For:</strong> ${d.ai_ideal_for}</p>
<h3>Specifications</h3>
<table>
<tr><td><strong>Item Type</strong></td><td>Fashion Jewellery</td></tr>
<tr><td><strong>Brand Name</strong></td><td>${d.vendor_name}</td></tr>
<tr><td><strong>Color</strong></td><td>${d.combine_color}</td></tr>
</table>`.replace(/\n/g,""); // removes all line breaks

return {
  body_html: html
};

let lastSku = null;
let variantNo = 0;

return items.map(item => {
  const currentSku = item.json.sku;

  // if SKU changes, reset counter
  if (currentSku !== lastSku) {
    variantNo = 1;
    lastSku = currentSku;
  } else {
    variantNo += 1;
  }

  return {
    json: {
      ...item.json,
      variant_no: variantNo
    }
  };
});

const inputItems = $input.all();

// ---- UPDATED COLOR MAP (SHORT FORM ‚Üí FULL NAME) ----
const colorMap = {
  t2: "2 tone",
  t3: "3 Tone",
  be: "Beige",
  bl: "Black",
  blu: "Blue",
  gbl: "Blue & Green",
  br: "Brown",
