  ru: "Ruby",
  sl: "Silver",
  sbl: "Sky Blue",
  wh: "White",
  ye: "Yellow",
  yg: "Yellow & Green",
  yp: "Yellow & Pink",
  yr: "Yellow & Red",
  ox: "Oxidised",
  ow: "Off White",
  mh: "Mehndi",
  mx: "Mix Color",
  gru: "Green & Ruby",
  wi: "Wine",
  rb: "Rainbow",
  co: "Copper",
  lv: "Lavender",
};

// ---- PROCESS EACH ITEM ----
const results = inputItems.map((item) => {
  const fileId = item.json.id;
  const fileName = item.json.name || "";

  const base = fileName.replace(/\.[^/.]+$/, "");

  // Extract color part (letters and hyphens at end)
  const match = base.match(/[A-Za-z-]+$/);
  const colorPart = match ? match[0] : "";

  // Split into codes
  const colorCodes = colorPart
    .split("-")
    .filter((c) => c.trim().length > 0);

  // Convert codes ‚Üí full names
  const colors = colorCodes.map((code) => {
    const normalized = code.toLowerCase();
    return colorMap[normalized] || code.toUpperCase();
  });

  // Combined color text
  let colorCombined = "";
  if (colors.length === 1) {
    colorCombined = colors[0];
  } else if (colors.length > 1) {
    colorCombined = `${colors.join(" and ")} Mix`;
  }

  // Extract SKU (everything before first letter)
  const skuMatch = base.match(/^[^A-Za-z]+/);
  const sku = skuMatch ? skuMatch[0] : "";

  return {
    json: {
      fileId,
      fileName,
      sku,
      colorCodes,
      colors,
      colorCombined,
    },
  };
});

return results;

const input = $input.first().json.output;

// 1. Convert technical specs to HTML table rows (filtering nulls)
const tableRows = Object.entries(input.technical_specs || {})
  .filter(([_, value]) => value !== null && value !== "")
  .map(
    ([key, value]) =>
      "<tr>" +
      "<td><strong>" +
      key.replace(/_/g, " ") +
      "</strong></td>" +
      "<td>" +
      value +
      "</td>" +
      "</tr>"
  )
  .join("");

// 2. Format description with <br><br> after sentences (no newlines)
const formattedDescription = input.description
  .replace(/\.\s+/g, ".<br><br>")
  .trim();

// 3. Build Shopify-safe HTML (single-line output)
const html_content =
  "<h2>" +
  input.product_name +
  "</h2>" +
  "<p>" +
  formattedDescription +
  "</p>" +
  "<p><strong>Ideal For:</strong> " +
  input.ideal_for +
  "</p>" +
  "<h3>Product Specifications</h3>" +
  "<table border='1' cellpadding='8' cellspacing='0' style='border-collapse:collapse;width:100%;'>" +
  "<tbody>" +
  tableRows +
  "</tbody>" +
  "</table>";

// 4. Return all original fields + enhanced HTML
return {
  ...input,
  html_content: html_content
};

