// Get the code snippet and language from previous node
const codeSnippet = $json.code_snippet || '';
const language = $json.code_language || 'javascript';

// Calculate dimensions based on content
const lines = codeSnippet.split('\n');
const lineCount = lines.length;
const maxLineLength = Math.max(...lines.map(line => line.length));

// Adjust sizing calculations
const charWidth = 8.4; // approximate width per character in monospace
const lineHeight = 24; // pixels per line
const containerPadding = 20; // padding inside code container
const bodyPadding = 10; // minimal body padding
const headerHeight = 40; // header with dots

// Calculate dimensions and FORCE INTEGERS (Math.ceil) to prevent API errors
const calculatedWidth = Math.max(400, maxLineLength * charWidth + (containerPadding * 2));
const contentWidth = Math.ceil(Math.min(calculatedWidth, 1200)); 
const contentHeight = Math.ceil((lineCount * lineHeight) + headerHeight + (containerPadding * 2));

// Create HTML with minimal padding, sized to content
const html = `
<!DOCTYPE html>
<html>
<head>
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <link href="https://cdnjs.cloudflare.com/ajax/libs/prism/1.29.0/themes/prism-tomorrow.min.css" rel="stylesheet" />
  <link rel="preconnect" href="https://fonts.googleapis.com">
  <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
  <link href="https://fonts.googleapis.com/css2?family=Fira+Code:wght@400;500&display=swap" rel="stylesheet">
  <style>
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }
    html, body {
      width: 100%;
      height: 100%;
      margin: 0;
      padding: 0;
    }
    body {
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      font-family: 'Fira Code', 'Consolas', monospace;
      padding: ${bodyPadding}px;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    .code-container {
      background: #2d2d2d;
      border-radius: 10px;
      padding: ${containerPadding}px;
      box-shadow: 0 10px 30px rgba(0,0,0,0.4);
      width: auto;
      max-width: 100%;
    }
    .header {
      display: flex;
      gap: 8px;
      margin-bottom: 15px;
    }
    .dot {
      width: 12px;
      height: 12px;
      border-radius: 50%;
    }
    .dot-red { background: #ff5f56; }
    .dot-yellow { background: #ffbd2e; }
    .dot-green { background: #27c93f; }
    pre {
      margin: 0;
      padding: 0;
      overflow-x: visible;
      white-space: pre;
      max-width: 100%;
    }
    code {
      font-size: 12px;
      line-height: 1.5;
      display: block;
      font-family: 'Fira Code', 'Consolas', monospace;
    }
    /* Override Prism's default margins */
    pre[class*="language-"] {
      margin: 0;
      padding: 0;
      background: transparent;
    }
  </style>
</head>
<body>
  <div class="code-container">
    <div class="header">
      <div class="dot dot-red"></div>
      <div class="dot dot-yellow"></div>
      <div class="dot dot-green"></div>
    </div>
    <pre><code class="language-${language}">${codeSnippet.replace(/</g, '&lt;').replace(/>/g, '&gt;')}</code></pre>
  </div>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/prism/1.29.0/prism.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/prism/1.29.0/components/prism-${language}.min.js"></script>
</body>
</html>
`;

return [{
  json: {
    ...items[0].json,
    html: html,
    estimatedWidth: contentWidth + (bodyPadding * 2),
    estimatedHeight: contentHeight + (bodyPadding * 2)
  }
}];

// Get the head_commit object from the webhook body
const headCommit = $json.body.head_commit;

// Get the arrays of added and modified files. Default to empty arrays if they don't exist.
const addedFiles = headCommit.added || [];
const modifiedFiles = headCommit.modified || [];

// Combine both arrays into a single list of file paths
const allChangedFiles = [...addedFiles, ...modifiedFiles];

// If there are no changed files, return an empty result to stop the workflow gracefully.
if (allChangedFiles.length === 0) {
  return [];
}

// Create a new list of n8n items. Each item will contain one file path.
const items = allChangedFiles.map(filePath => {
  return {
    json: {
      // We store the path in a new field called 'filePath'
      filePath: filePath
    }
  };
});

// Return the new list of items
return items;
