const input = $json || {};

const source = input.source || input.uat?.source || "webhook";
const testerName = input.tester_name || input.tester?.name || input.uat?.tester_name || "";
const testerEmail = input.tester_email || input.tester?.email || input.uat?.tester_email || "";
const messageRaw = input.message || input.text || input.feedback || input.uat?.message_raw || "";
const buildVersion = input.build_version || input.uat?.build_version || "unknown";
const pageUrl = input.page_url || input.uat?.page_url || "";
const screenshotUrl = input.screenshot_url || input.uat?.screenshot_url || "";

return {
  uat: {
    source,
    tester_name: testerName,
    tester_email: testerEmail,
    message_raw: messageRaw,
    build_version: buildVersion,
    page_url: pageUrl,
    screenshot_url: screenshotUrl,
    received_at: new Date().toISOString(),
  },
};

// Try multiple possible fields depending on node output
const raw =
  $json.output ||
  $json.text ||
  $json.message ||
  ($json.response?.text) ||
  ($json.data?.[0]?.content) ||
  ($json.response?.[0]?.message?.content) ||

  "";

let triage;
let parseOk = true;

try {
  triage = JSON.parse(raw);
} catch (e) {
  parseOk = false;
  triage = {
    sentiment: "Negative",
    type: "Noise",
    severity: "Minor",
    summary: "AI response could not be parsed as JSON.",
    components: ["other"],
    repro_steps: [],
    suggested_title: "UAT feedback (manual triage)",
    confidence: 0,
  };
}

// Normalize + validate
const allowedType = ["CriticalBug", "UXImprovement", "FeatureRequest", "Noise"];
if (!allowedType.includes(triage.type)) triage.type = "Noise";

const allowedSeverity = ["Blocker", "Critical", "Major", "Minor"];
if (!allowedSeverity.includes(triage.severity)) triage.severity = "Minor";

const allowedSentiment = ["Positive", "Negative"];
if (!allowedSentiment.includes(triage.sentiment)) triage.sentiment = "Negative";

triage.confidence = Math.max(0, Math.min(1, Number(triage.confidence ?? 0)));

return {
  ...$json,
  triage: {
    ...triage,
    parse_ok: parseOk,
  },
};

