const msg = $json.uat?.message_raw || "";

const cleaned = msg
  .replace(/<[^>]*>/g, " ")
  .replace(/\s+/g, " ")
  .trim()
  .slice(0, 3000);

return {
  ...$json,
  uat: {
    ...$json.uat,
    message_clean: cleaned,
  },
};

const triage = $json.triage || {};
const uat = $json.uat || {};
const cfg = $json.cfg || {};

const reproSteps = Array.isArray(triage.repro_steps) && triage.repro_steps.length
  ? triage.repro_steps.map(s => `- ${s}`).join('\n')
  : '- (none provided / not inferable)';

const subject = `Manual UAT triage needed (confidence ${triage.confidence ?? 0})`;

const body =
`Hi team,

Manual review is needed for this UAT feedback (AI confidence: ${triage.confidence ?? 0}, parse_ok: ${triage.parse_ok ?? false}).

