function addTrailingSlash(str) {
  if (typeof str !== 'string') {
    return str; // Or throw an error, handle non-string inputs
  }
  if (!str.endsWith('/')) {
    return str + '/';
  }
  return str;
}

const urls = []
for (const item of $input.first().json['Page URLs'].split(',')) {
  urls.push({ url: addTrailingSlash(item).trim()})
}

return urls;
