const customerName = $json["Customer Name"];
const phone = $json["Phone Number"];
const product = $json.Product;
const address = $json["Customer Address"];
const returnReason = $json["Return Reason"];
const pickupDate = $json["Pickup Date"];
const orderId = $json["Order ID"];

// SMS / WhatsApp message
const message = `Hello ${customerName},\n\nThis is a reminder that your return pickup for ${product} is scheduled for today.\n\nPickup Address: ${address}\nReturn Reason: ${returnReason}\n\nPlease keep your package ready for collection.Pickup will be completed before 11PM.\n\nThank you!`;

// VOICE MESSAGE (for Twilio <Say>)
const voiceMessage = 
  `Hello ${customerName}. ` +
  `This is a reminder that your return pickup for ${product} is scheduled today. ` +
  `The pickup address is ${address}. ` +
  `and pickup will be completed before 11PM. ` +
  `Please keep your package ready for collection. Thank you.`;

// Return both
return {
  message_text: message,
  voice_message: voiceMessage,
  phone: phone,
  order_id: orderId,
  customer_name: customerName,
  product: product,
  row_id: $input.item.json.row_id
};
