// List certifications you want to track
return [
  {
    json: {
      certId: 'pmp',
      url: 'https://www.pmi.org/certifications/pmp'
    }
  },
  {
    json: {
      certId: 'cissp',
      url: 'https://www.isc2.org/Certifications/CISSP'
    }
  }
];

const [current, previous] = $input.all();

const scraped = current.json;
const prevContent = previous.json;
let prevParsed;
try {
  prevParsed = prevContent ? JSON.parse(prevContent) : null;
} catch (_) {
  prevParsed = null;
}

const changed = !prevParsed || JSON.stringify(scraped) !== JSON.stringify(prevParsed);

return [{
  json: {
    ...scraped,
    certId: current.json.certId,
    changed,
    prevData: prevParsed || null
  }
}];
