const staticData = $getWorkflowStaticData('global');
const lastProcessed = staticData.lastProcessedTime || '1970-01-01T00:00:00.000Z';
const newItems = [];
let maxDate = lastProcessed;

for (const item of $input.all()) {
  // indexedAt is the safest timestamp to use for notifications
  const notifTime = item.json.indexedAt; 
  if (notifTime > lastProcessed) {
    newItems.push(item);
    if (notifTime > maxDate) {
      maxDate = notifTime;
    }
  }
}

// Update memory immediately so we don't process these again
staticData.lastProcessedTime = maxDate;

return newItems;

