// Get the text output from the LLM
const text = $input.first().json.text;

// Remove markdown code blocks (```json and ```)
const cleanedText = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

// Parse the JSON
const parsedData = JSON.parse(cleanedText);

// Return the parsed object
return [{
  json: parsedData
}];

// Extract and clean the Veo 3 prompt text from LLM output
const text = $input.first().json.text;

// Remove any template variables that weren't resolved
// The prompt should be plain text, not contain {{ }} variables
const cleanedPrompt = text
  .trim()
  .replace(/\{\{[^}]+\}\}/g, '') // Remove any unresolved template variables
  .replace(/\s+/g, ' ')           // Replace multiple spaces with single space
  .trim();

// Get data from previous steps in the workflow
// This assumes the data flows through from the Google Sheets node
const allData = $input.all();
const previousData = allData[0]?.json || {};

// Extract aspect ratio and duration from final_output if available
let aspectRatio = '9:16'; // default
let duration = '60s';      // default

if (previousData.final_output) {
  aspectRatio = previousData.final_output.includes('9:16') ? '9:16' : '16:9';

  // Try to extract duration
  const durationMatch = previousData.final_output.match(/(\d+)-(\d+)\s*seconds?/i);
  if (durationMatch) {
    const avgDuration = Math.floor((parseInt(durationMatch[1]) + parseInt(durationMatch[2])) / 2);
    duration = `${avgDuration}s`;
  }
}

// Return properly formatted data for Veo 3 API
return [{
  json: {
    id: previousData.id || 'VEO_' + Date.now(),
    veo3_prompt: cleanedPrompt,
    aspect_ratio: aspectRatio,
    duration: duration,
    caption: previousData.caption || '',
    timestamp: new Date().toISOString()
  }
}];

