// Function (nicht Function Item)
return items.flatMap(item => {
  const bin = item.binary || {};
  const keys = Object.keys(bin).filter(k => k.startsWith('attachment_')).sort();
  return keys.map(k => ({
    json: {
      attachmentKey: k,
      fileName: bin[k]?.fileName,
      mimeType: bin[k]?.mimeType,
    },
    binary: { data: bin[k] }, // jetzt heisst die Binary-Property überall 'data'
  }));
});
