const input = items[0].json; 
const servers = input.data;

const mode = $('Auto Config').first().json.mode || "auto"; 

if (!servers || servers.length === 0) {
  return { 
    success: false, 
    result: "No servers found for this Place ID." 
  };
}

let bestServer = servers[0];

for (let i = 1; i < servers.length; i++) {
  const current = servers[i];

  if (mode === "ping") {
    if (current.ping < bestServer.ping) {
      bestServer = current;
    }
  } 
  else if (mode === "latency") {
    if (current.ping < bestServer.ping) {
      bestServer = current;
    } else if (current.ping === bestServer.ping) {
      if (current.fps > bestServer.fps) {
        bestServer = current;
      }
    }
  } 
  else {
    const pingDiff = current.ping - bestServer.ping;

    if (pingDiff < -5) {
      bestServer = current;
    } else if (Math.abs(pingDiff) <= 5) {
      if (current.playing > bestServer.playing) {
        bestServer = current;
      }
    }
  }
}

return {
  success: true,
  selected_mode: mode,
  best_server_id: bestServer.id,
  stats: {
    ping: bestServer.ping,
    fps: Math.round(bestServer.fps * 100) / 100,
    players: bestServer.playing,
    maxPlayers: bestServer.maxPlayers
  },
  all_details: bestServer
};
