// Parse and filter active disaster alerts
const weatherAlerts = $input.first().json.weatherAlerts || [];
const seismicAlerts = $input.first().json.seismicAlerts || [];
const floodAlerts = $input.first().json.floodAlerts || [];

// Combine all alerts
const allAlerts = [
  ...weatherAlerts.map(a => ({...a, type: 'weather'})),
  ...seismicAlerts.map(a => ({...a, type: 'seismic'})),
  ...floodAlerts.map(a => ({...a, type: 'flood'}))
];

// Filter for active/severe alerts only
const activeAlerts = allAlerts.filter(alert => {
  const isActive = alert.status === 'active' || alert.active === true;
  const isSevere = ['severe', 'extreme', 'warning'].some(keyword => 
    JSON.stringify(alert).toLowerCase().includes(keyword)
  );
  return isActive && isSevere;
});

// Extract affected zones/coordinates
const affectedAreas = activeAlerts.map(alert => ({
  alertId: alert.id || alert.guid,
  alertType: alert.type,
  severity: alert.severity || 'unknown',
  area: alert.area || alert.geometry || alert.location,
  coordinates: alert.coordinates || alert.geometry?.coordinates,
  description: alert.description || alert.title,
  timestamp: alert.timestamp || alert.pubDate || new Date().toISOString()
}));

return affectedAreas.map(area => ({ json: area }));

// Map properties to affected disaster zones
const alerts = $input.first().json;
const properties = $('üìä Get Property Database').all();

// Function to check if property is in affected area
function isPropertyAffected(property, alert) {
  // Simple proximity check (in real scenario, use proper geo-spatial library)
  if (alert.coordinates && property.latitude && property.longitude) {
    const distance = Math.sqrt(
      Math.pow(alert.coordinates[0] - property.longitude, 2) +
      Math.pow(alert.coordinates[1] - property.latitude, 2)
    );
    return distance < 0.5; // ~50km radius approximation
  }

  // Fallback: check if property's city/region matches alert area
  if (alert.area && property.city) {
    return alert.area.toLowerCase().includes(property.city.toLowerCase());
  }

  return false;
}

// Map affected properties
const affectedProperties = [];

for (const property of properties) {
  const relevantAlerts = [];

  // Check each alert
  for (const alert of Array.isArray(alerts) ? alerts : [alerts]) {
    if (isPropertyAffected(property.json, alert)) {
      relevantAlerts.push(alert);
    }
  }

  // If property is affected, add to output
  if (relevantAlerts.length > 0) {
    affectedProperties.push({
      json: {
        ...property.json,
        affectedAlerts: relevantAlerts,
        alertCount: relevantAlerts.length,
        highestSeverity: Math.max(...relevantAlerts.map(a => 
          a.severity === 'extreme' ? 3 : a.severity === 'severe' ? 2 : 1
        ))
      }
    });
  }
}

return affectedProperties;

// Calculate preliminary insurance claim estimates
const properties = $input.all();

const claimCalculations = properties.map(item => {
  const property = item.json;

  // Base calculations
  const propertyValue = property.property_value || 500000;
  const severityMultiplier = {
    3: 0.30,  // Extreme: 30% damage
    2: 0.15,  // Severe: 15% damage
    1: 0.05   // Moderate: 5% damage
  }[property.highestSeverity] || 0.05;

  const estimatedDamage = propertyValue * severityMultiplier;
  const deductible = property.insurance_deductible || 5000;
  const claimAmount = Math.max(0, estimatedDamage - deductible);

  // Determine damage level
  const damageLevel = property.highestSeverity === 3 ? 'Critical' :
                      property.highestSeverity === 2 ? 'Severe' : 'Moderate';

  // Schedule maintenance window (24-72 hours from now based on severity)
  const hoursUntilMaintenance = property.highestSeverity === 3 ? 4 :
                                 property.highestSeverity === 2 ? 24 : 72;
  const maintenanceStart = new Date(Date.now() + hoursUntilMaintenance * 3600000);
  const maintenanceEnd = new Date(maintenanceStart.getTime() + 2 * 3600000); // 2 hour window

  return {
    json: {
      ...property,
      estimated_repair_cost: Math.round(estimatedDamage),
      insurance_claim_amount: Math.round(claimAmount),
      damage_level: damageLevel,
      maintenance_start_time: maintenanceStart.toISOString(),
      maintenance_end_time: maintenanceEnd.toISOString(),
      disaster_type: property.affectedAlerts[0]?.type || 'unknown',
      disaster_description: property.affectedAlerts.map(a => a.description).join('; ')
    }
  };
});

return claimCalculations;
