parameters.text:
=Analyze the following tenant data and predict default risk:

Rent Payment History: {{ $json.rentPaymentData }}

Credit Bureau Data: {{ $json.creditBureauData }}

Employment Records: {{ $json.employmentData }}

parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"riskScore": {
			"type": "number",
			"description": "Risk score from 0-100, where 100 is highest risk"
		},
		"riskLevel": {
			"type": "string",
			"description": "Risk category: LOW, MEDIUM, or HIGH"
		},
		"defaultProbability": {
			"type": "number",
			"description": "Probability of default as percentage (0-100)"
		},
		"recommendedAction": {
			"type": "string",
			"description": "Recommended action: MONITOR, CONTACT, or COLLECTION"
		},
		"keyRiskFactors": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "List of key risk factors identified"
		},
		"tenantId": {
			"type": "string",
			"description": "Tenant identifier"
		}
	}
}

parameters.text:
