const msg = $json.uat?.message_raw || "";

const cleaned = msg
  .replace(/<[^>]*>/g, " ")
  .replace(/\s+/g, " ")
  .trim()
  .slice(0, 3000);

return {
  ...$json,
  uat: {
    ...$json.uat,
    message_clean: cleaned,
  },
};

