return items.map(item => {
  const p = item.json.properties || {};
  const domain =
    p.domain?.value ||
    p.website?.value ||
    "";

  return {
    json: {
      domain: domain
    }
  };
});

const TOP_PERCENT = 5;
const companies = items.map(i => i.json);


companies.sort((a, b) => {
  const revA = parseFloat(a.properties?.annualrevenue || 0);
  const revB = parseFloat(b.properties?.annualrevenue || 0);
  return revB - revA;
});

const count = Math.max(1, Math.floor(companies.length * (TOP_PERCENT / 100)));

const topCompanies = companies.slice(0, count);
return topCompanies.map(c => ({ json: c }));
