const items = $input.all();
const searchQueries = [];
const locations = new Set();
const warningTypes = new Set();

items.forEach(item => {
  const data = item.json;

  if (data.heading_en === "No Advisory") {
    return;
  }

  const warningType = data.heading_en;
  warningTypes.add(warningType);

  const locationMatches = data.text_en.match(/(?:states of|over the waters of|waters of)\s+([^.]+?)(?:until|;)/gi);

  if (locationMatches) {
    locationMatches.forEach(match => {
      const locationText = match.replace(/(?:states of|over the waters of|waters of)/gi, '').replace(/until|;/gi, '').trim();
      const stateMatches = locationText.match(/([A-Z][a-z]+(?:\s+[A-Z][a-z]+)*)/g);

      if (stateMatches) {
        stateMatches.forEach(state => {
          if (state.length > 2) {
            locations.add(state.trim());
          }
        });
      }
    });
  }

  const severity = data.heading_en.includes('Severe') ? 'severe' : 
                   data.heading_en.includes('Alert') ? 'alert' : '';

  const baseQuery = warningType.replace(/\(.*?\)/g, '').trim();

  searchQueries.push({
    json: {
      searchQuery: `${baseQuery} Malaysia news`,
      warningType: warningType,
      severity: severity,
      locations: Array.from(locations).join(', '),
      dateRange: 'past 24 hours'
    }
  });
});

const uniqueQueries = [];
const seen = new Set();

searchQueries.forEach(query => {
  const key = query.json.searchQuery;
  if (!seen.has(key)) {
    seen.add(key);
    uniqueQueries.push(query);
  }
});

return uniqueQueries.slice(0, 3);

const items = $input.all();
const urls = [];

items.forEach(item => {
  const urlMatches = item.json.output?.match(/https?:\/\/[^\s\)]+/g);
  if (urlMatches) {
    urlMatches.forEach(url => {
      const cleanUrl = url.replace(/[,.\)]+$/, '');
      if (!urls.includes(cleanUrl)) {
        urls.push(cleanUrl);
      }
    });
  }
});

return urls.map(url => ({ json: { url: url } }));
