const urls = [
  "https://www.commercialsite1.com/office-space/xyz",
  "https://www.commercialsite2.com/listings/xyz",
  "https://brokerportal.example.com/spaces?city=xyz&type=office"
];
return urls.map(url => ({ json: { url } }));

// Flatten and normalise listings
const items = $input.all();
let listings = [];
for (const item of items) {
  const sourceUrl = item.json.url || item.json.source || '';
  const results = item.json.listings || [];
  for (const entry of results) {
    listings.push({
      json: {
        listing_id: entry.id || entry.listing_id || entry.listing_url || entry.url,
        address: entry.address || '',
        price: entry.price || null,
        size_sqft: entry.size_sqft || null,
        listing_url: entry.listing_url || entry.url || '',
        broker_name: entry.broker_name || '',
        broker_phone: entry.broker_phone || '',
        availability_date: entry.availability_date || null,
        source: sourceUrl,
        scraped_at: new Date().toISOString()
      }
    });
  }
}
return listings;

const [listingData, baserowResponse] = $input.all().map(i => i.json);
const existing = Array.isArray(baserowResponse.results) ? baserowResponse.results : [];
let action = 'create';
let rowId = null;
if (existing.length > 0) {
  rowId = existing[0].id;
  const changed = existing[0].price !== listingData.price || existing[0].size_sqft !== listingData.size_sqft || existing[0].availability_date !== listingData.availability_date;
  action = changed ? 'update' : 'skip';
}
return [{ json: { ...listingData, _action: action, _rowId: rowId } }];
