/*
 * Return one item per certification-body URL.
 * Replace the sample URLs with the real pages you want to monitor.
 */
return [
  { json: { url: 'https://example-certbody1.org/requirements' } },
  { json: { url: 'https://example-association2.com/certification-updates' } }
];

/*
 * Normalise and enrich each scraped item.
 * Adds a hash for quick diffing.
 */
const crypto = require('crypto');
return $input.all().map(item => {
  const data = item.json;
  const hash = crypto.createHash('sha256').update(data.certification_name + data.requirement_text).digest('hex');
  return {
    json: {
      certificationName: data.certification_name,
      requirementText: data.requirement_text,
      lastUpdated: new Date(data.last_updated).toISOString(),
      hash
    }
  };
});

// Aggregate all items into one object for easy comparison
const items = $input.all();
return [
  {
    json: {
      currentRequirements: items.map(i => i.json)
    }
  }
];

/*
 * Input[0] = current data, Input[1] = baseline file data.
 */
const [currentItem, baselineItem] = $input.all();
const currentReq = currentItem.json.currentRequirements;
let baselineReq = [];
try {
  if (baselineItem.json.content) {
    baselineReq = JSON.parse(Buffer.from(baselineItem.json.content, 'base64').toString());
  }
} catch (e) {
  baselineReq = [];
}
const changed = JSON.stringify(currentReq) !== JSON.stringify(baselineReq);
return [{
  json: {
    changed,
    diff: changed ? currentReq : [],
    currentRequirements: currentReq
  }
}];
